graph TD
    A[Start: Entity Assessment] --> B{Are you a foreign government?};
    B -- Yes --> C[Potentially Applicable Person if imposing/benefiting from Unfair Foreign Tax];
    B -- No --> D{Are you a foreign individual or corporation?};
    D -- Yes --> E{Making payments subject to UFT or receiving benefits from UFT regime?};
    E -- Yes --> F[Potentially Applicable Person];
    E -- No --> G[Likely Not an Applicable Person based on this path];
    D -- No --> H{Other entity specified by Treasury?};
    H -- Yes --> I[Potentially Applicable Person];
    H -- No --> J[Likely Not an Applicable Person based on this path];
    C --> K[End Assessment];
    F --> K;
    G --> K;
    I --> K;
    J --> K;
